package protocol

import (
	"errors"
	"time"
)

type callAuction struct{}

/*
Frame
0c00000000011e001e006a05000030303030303100000000030000000000000000000000f4010000
*/
func (callAuction) Frame(code string) (*Frame, error) {
	exchange, number, err := DecodeCode(code)
	if err != nil {
		return nil, err
	}

	codeBs := []byte(number)
	codeBs = append(codeBs, []byte{
		0x00, 0x00, 0x00, 0x00,
		0x03, 0x00, 0x00, 0x00,
		0x00, 0x00, 0x00, 0x00,
		0x00, 0x00, 0x00, 0x00,
		0xf4, 0x01, 0x00, 0x00}...)
	return &Frame{
		Control: Control01,
		Type:    TypeCallAuction,
		Data:    append([]byte{exchange.Uint8(), 0x0}, codeBs...),
	}, nil
}

/*
Decode


2b02 00003c41 d5020000 f4fcffff 0000
2b02 33333b41 98030000 79feffff 0009
2b02 33333b41 ac030000 79feffff 0012
2b02 295c3b41 ed050000 25f6ffff 001b
2b02 295c3b41 ed050000 8cf6ffff 0024
2b02 295c3b41 ed050000 86f6ffff 002d
2b02 33333b41 d9030000 37feffff 0036
2c02 33333b41 d9030000 33feffff 000c
2c02 33333b41 d9030000 32feffff 0015
2c02 33333b41 d9030000 2ffeffff 001e
2c02 33333b41 d9030000 c9fdffff 0027
2c02 33333b41 ee030000 d5fdffff 0030
2c02 33333b41 ee030000 d3fdffff 0039
2d02 33333b41 ee030000 a1fdffff 0006
2d02 33333b41 ee030000 d9fcffff 000f
2d02 33333b41 ee030000 d7fcffff 0018
2d02 33333b41 ee030000 cdfcffff 002a
2d02 33333b41 ee030000 4dfcffff 0033
2e02 3d0a3b41 1a060000 9e010000 0000
*/
func (callAuction) Decode(bs []byte) (*CallAuctionResp, error) {

	if len(bs) < 2 {
		return nil, errors.New("数据长度不足")
	}

	_count := Uint16(bs[:2])
	resp := &CallAuctionResp{
		Count: _count,
		List:  make([]*CallAuction, 0, _count),
	}

	bs = bs[2:]

	now := time.Now()
	for i := uint16(0); i < resp.Count; i++ {

		n := Uint16(bs[:2])

		_hour := int(n / 60)
		_minute := int(n % 60)

		a := &CallAuction{
			Price:     Price(Uint32(bs[4:6])),
			Match:     Uint32(bs[6:8]),
			Unmatched: Uint32(bs[10:12]),
		}

		_ = bs[2:6]
		_ = bs[6:10]
		_ = bs[10:14]
		_ = bs[14]

		_second := int(bs[15])

		a.Time = time.Date(now.Year(), now.Month(), now.Day(), _hour, _minute, _second, 0, now.Location())
		resp.List = append(resp.List, a)

		bs = bs[16:]
	}

	return resp, nil
}

type CallAuctionResp struct {
	Count uint16
	List  []*CallAuction
}

type CallAuction struct {
	Time      time.Time //时间
	Price     Price     //价格
	Match     uint32    //匹配量
	Unmatched uint32    //未匹配量
}
